import numpy as np

# Таблиця значень функції (варіант 8)
x_values = np.array([1.5, 2.0, 2.5, 3.0, 3.5, 4.0])
y_values = np.array([10.517, 10.193, 9.807, 9.387, 8.977, 8.637])

# Крок таблиці
h = x_values[1] - x_values[0]

# Обчислення кінцевих різниць
def finite_differences(y):
    n = len(y)
    delta_y = np.zeros((n, n))
    delta_y[:, 0] = y
    for j in range(1, n):
        for i in range(n - j):
            delta_y[i, j] = delta_y[i + 1, j - 1] - delta_y[i, j - 1]
    return delta_y

# Таблиця кінцевих різниць
delta_table = finite_differences(y_values)
print("Таблиця кінцевих різниць:")
np.set_printoptions(precision=4, suppress=True)
print(delta_table)

# Перші різниці біля точки x=3.0 (індекс 3)
delta_y1  = delta_table[2, 1]
delta2_y1 = delta_table[1, 2]
delta3_y1 = delta_table[0, 3]
delta4_y1 = delta_table[0, 4]

# Формули Ньютона для похідних
def first_derivative(delta_y1, delta2_y1, delta3_y1, delta4_y1, h):
    return (delta_y1 - delta2_y1 / 2 + delta3_y1 / 3 - delta4_y1 / 4) / h

def second_derivative(delta2_y1, delta3_y1, delta4_y1, h):
    return (delta2_y1 - delta3_y1 + 11 * delta4_y1 / 12) / h**2

# Обчислення y'(3.0) та y''(3.0)
y_prime = first_derivative(delta_y1, delta2_y1, delta3_y1, delta4_y1, h)
y_double_prime = second_derivative(delta2_y1, delta3_y1, delta4_y1, h)

# Округлення до 0.001
y_prime = round(y_prime, 3)
y_double_prime = round(y_double_prime, 3)

print(f"\nПерша похідна y'(3.0) ≈ {y_prime}")
print(f"Друга похідна y''(3.0) ≈ {y_double_prime}")

delta_table

