#Лабораторна робота 3. Метод Ньютона (дотичних). Комбінований метод

import numpy as np
import numdifftools as nd

def f(x):
    return x**4 - 12*x**3 - 9

def nuton(a, b, eps, f):
    df1 = nd.Derivative(f, n=1)
    df2 = nd.Derivative(f, n=2)
    xi = b if f(b) * df2(b) > 0 else a

    xi_1 = xi - f(xi) / df1(xi)
    while np.abs(xi_1 - xi) > eps:
        xi = xi_1
        d = df1(xi)
        if d == 0:
            raise ZeroDivisionError("Похідна дорівнює нулю — зупинка.")
        xi_1 = xi - f(xi) / d

    print('Метод Ньютона, x =', round(float(xi_1), 4))

def komb(a, b, eps, f):
    df2 = nd.Derivative(f, n=2)(a)
    if f(a) * df2 > 0:
        a0, b0 = a, b
    else:
        a0, b0 = b, a
    ai, bi = a0, b0

    while np.abs(ai - bi) > eps:
        ai_1 = ai - f(ai) * (bi - ai) / (f(bi) - f(ai))
        bi_1 = bi - f(bi) / nd.Derivative(f, n=1)(bi)
        ai, bi = ai_1, bi_1

    x = (ai_1 + bi_1) / 2

    print('Комбінований метод, x =', round(float(x), 4))

if __name__ == "__main__":
    a1, b1 = 12, 13
    eps = 0.001

    print("Розв'язання на відрізку [12, 13]")
    nuton(a1, b1, eps, f)
    komb(a1, b1, eps, f)

